import axios from 'axios';
import { useQuery, useMutation } from '@apollo/react-hooks';
import {
  GET_PORTFOLIOS,
  CREATE_PORTFOLIO,
  UPDATE_PORTFOLIO,
  DELETE_PORTFOLIO } from '@/apollo/queries';
import PortfolioCard from "@/components/portfolios/portfolioCard.js"
import Link from 'next/link';
import withApollo from '@/hoc/withApollo';
import { getDataFromTree } from '@apollo/react-ssr';




const graphDeletePortfolio = (id) => {
  const query = `
  mutation deletePortfolio {
    deletePortfolio(id: "${id}")
  }`;
  return axios.post('http://localhost:3000/graphql', { query })
    .then(({data: graph}) => graph.data)
    .then(data => data.deletePortfolio)
}


const Portfolios = () =>{

const {data} = useQuery(GET_PORTFOLIOS)
const [updatePortfolio] = useMutation(UPDATE_PORTFOLIO)
const [deletePortfolio] = useMutation(DELETE_PORTFOLIO,{
  update(cache, {data: {deletePortfolio}}){
    const {portfolios} = cache.readQuery({query : GET_PORTFOLIOS})
    const newPortfolios = Portfolios.filter
  }
}
  )
const [createPortfolio] = useMutation(CREATE_PORTFOLIO,{
  update(cache, {data : {createPortfolio}}){
    // get data from the cache
    const {portfolios} = cache.readQuery({query : GET_PORTFOLIOS})
    //write on the cache the same data with the new data that we create
    cache.writeQuery({
      query: GET_PORTFOLIOS,
      data: {portfolios : [...portfolios, createPortfolio]}
    })
  }
})


  const portfolios = data && data.portfolios || [];
  return(
    <>
      <section className="section-title">
        <div className="px-2">
          <div className="pt-5 pb-4">
            <h1>Portfolios</h1>
          </div>
        </div>
      </section>
      <button
        onClick={createPortfolio}
        className="btn btn-primary">
        create protfolio
      </button>
      <section className="pb-5">
        <div className="row">
          {portfolios.map(portfolio =>(
            <div key={portfolio._id} className="col-md-4">
              <Link
                href={`/portfolios/[id]`}
                as={`/portfolios/${portfolio._id}`}>
                <a className='card-link'><PortfolioCard portfolio={portfolio} /></a>
              </Link>
              <button
              className="btn btn-warning"
              onClick={() => updatePortfolio({variables: {id: portfolio._id}})}>
              update portfolio</button>
              <button
              onClick={() =>deletePortfolio({variables: {id: portfolio._id}})}
              className="btn btn-danger"
              >
              delete portfolio
              </button>
            </div>
          ))}

        </div>
      </section>
    </>
    )
}
// Portfolios.getInitialProps = async () =>{
//   const portfolios = await fetchPortfolios()
//   return {
//     data: {portfolios}
//   }
// }

export default withApollo(Portfolios, { getDataFromTree })
